import React from 'react';
import styled from 'styled-components';
import 'bootstrap/dist/css/bootstrap.min.css';

function Landing() {
    return (
        <Container>
            <Logo src="/images/logo.svg"/>
            <LoginContainer>
                <Login className="btn btn-danger">Login</Login>
            </LoginContainer>
        </Container>
    )
}

export default Landing

const Container = styled.div`
    display: flex;
    align-items: center;
    &:before {
        background: url("/images/slider-badag.jpg") center center / cover no-repeat fixed;
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        z-index: -1;
    }
    height: 100vh;
    overflow-y: hidden;
`;

const Logo = styled.img`
    width: 80px;
`;

const Login = styled.div`
    padding: 5px 20px 5px 20px;
    background: #279aff;
    border: 1px solid #279aff;

    &:hover {
        background: #2386dc;
        border: 1px solid #2386dc;
    }
`;

const LoginContainer = styled.div`
    display: flex;
    justify-content: flex-end;
    flex: 1;
`;
