    border-radius: 4px;
    text-align: center;
    font-size: 18px;
    cursor: pointer;
    margin-top: 8px;
    margin-bottom: 12px;
    transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
