import React from 'react';
import styled from 'styled-components';
import 'bootstrap/dist/css/bootstrap.min.css';
import Viewers from './Viewers';

function Landing() {
    return (
        <Container className="d-block">
            <Nav className="d-flex">
                <Logo src="/images/logo.svg"/>
                <LoginContainer>
                    <Login className="btn btn-danger">Login</Login>
                </LoginContainer>
            </Nav>
            <MovieInfo className="d-block">
                <MovieLogo src="/images/lucialogo.png"/>
                <Year className="d-flex">
                    <span>12 Mar 2021</span>
                    <p>13+</p>
                </Year>
                <MovieButtons>
                    <WatchButton className="d-flex">
                        <img src="/images/play-icon-white.png"/>
                        <span>WATCH NOW</span>
                    </WatchButton>
                    <ListButton className="d-flex">
                        <img src="/images/play-icon-white.png"/>
                        <span>TRAILER</span>
                    </ListButton>
                </MovieButtons>
            </MovieInfo>
            <Viewers/>
        </Container>
    )
}

export default Landing

const Container = styled.div`
    display: flex;
    align-items: center;
    height: 100vh;
    &:before {
        background: url("/images/lucia.gif") center center / cover no-repeat fixed;
        content: "";
        position: absolute;
        top: 0;
        left: 0;
        right: 0;
        bottom: 0;
        z-index: -2;
    }
`;

const Logo = styled.img`
    width: 80px;
`;

const Login = styled.div`
    padding: 5px 20px 5px 20px;
    background: #279aff;
    border: 1px solid #279aff;

    &:hover {
        background: #2386dc;
        border: 1px solid #2386dc;
    }
`;

const LoginContainer = styled.div`
    display: flex;
    justify-content: flex-end;
    align-items: center;
    flex: 1;
`;

const Nav = styled.div`
    margin-top: 20px;
`;

const MovieInfo = styled.div``;

const MovieLogo = styled.img`
    width: 25%;
    margin-top: 70px;
`;

const Year = styled.div`
    margin-top: 5px;
`;

const MovieButtons = styled.div`
    display: flex;
`;

const WatchButton = styled.button`
    padding: 10px 25px 10px 25px;
    background: rgba(0, 0, 0, 1);
    border-radius: 3px;
    border-width: 0px;
    align-items: center;
    justify-content: center;
    margin-right: 10px;
`;

const ListButton = styled(WatchButton)`
    background: rgba(0, 0, 0, .5);
`;
