import React from 'react'
import styled from 'styled-components';
import HoverVideoPlayer from 'react-hover-video-player';

function Viewers() {
    return (
        <Container>
            <Wrap>
                <HoverVideoPlayer
                videoSrc="/videos/luciawater.mp4"
                pausedOverlay={
                    <img
                    src="/images/ep1.jpg"
                    alt=""
                    />
                }
                loadingOverlay={
                    <div className="loading-spinner-overlay" />
                }
                />
                <p>01</p>
            </Wrap>
            <Wrap>
                <img src="/images/ep2.jpg"/>
                <p>02</p>
            </Wrap>
            <Wrap>
                <img src="/images/ep3.png"/>
                <p>03</p>
            </Wrap>
            <Wrap>
                <img src="/images/ep4.jpeg"/>
                <p>04</p>
            </Wrap>
            <Wrap>
                <img src="/images/ep5.png"/>
                <p>05</p>
            </Wrap>
        </Container>
    )
}

export default Viewers

const Container = styled.div`
    margin-top: 1vh;
    display: grid;
    padding: 7px 0px 26px;
    grid-gap: 25px;
    grid-template-columns: repeat(5, minmax(0, 1fr));
`;

const Wrap = styled.div`
    cursor: pointer;
    border-radius: 5px;
    box-shadow: rgb(0 0 0 / 69%) 0px 26px 30px -10px, rgb(0 0 0 / 73%) 0px 16px 10px -10px;
    transition: all 250ms cubic-bezier(0.25, 0.46, 0.45, 0.94) 0s;
    height: 75%;
    position: relative;

    img {
        width: 100%;
        height: 100%;
        object-fit: cover;
        border-radius: 5px;
        background: -webkit-linear-gradient(top, 
            rgba(0,0,0,0.9) 0%, 
            rgba(0,0,0,0) 20%,
            rgba(0,0,0,0) 80%,
            rgba(0,0,0,0.7) 100%
          ),
          -webkit-linear-gradient(left, 
            rgba(0,0,0,0.9) 0%, 
            rgba(0,0,0,0) 20%,
            rgba(0,0,0,0) 80%,
            rgba(0,0,0,0.7) 100%
          );
    }

    &:hover {
        box-shadow: rgb(0 0 0 / 80%) 0px 40px 58px -16px, rgb(0 0 0 / 72%) 0px 30px 22px -10px;
        transform: scale(1.19);
    }

    P {
        position: absolute;
        color: #fff;
        font-weight: bold;
        margin-top: -40px;
        margin-left: 13px;
        font-size: 20px;
    }
`;
